<script>
    $('.btn-open-menu').on('click', function(e) {
        e.preventDefault();
        $('body').toggleClass('open');
    });
    $(".scroll-link").on("touchend, click", function (event) {
        event.preventDefault();
        var id  = $(this).attr('href'),
            top = $(id).offset().top ;
        $('body,html').animate({scrollTop: top}, 1500);
    });
      
    document.addEventListener( 'wpcf7submit', function( event ) {
        console.log(event);
        if (event.detail.status == "mail_sent") {
            $('#thankModal').modal('show');
            $('.add-api-box-form div.wpcf7-mail-sent-ok').hide();
            console.log("SUCCESS");
            if (event.detail.contactFormId == "210" || event.detail.contactFormId == 214) {
                console.log('call');
                dataLayer.push({'formOK':'ok', 'event':'callOK'});
            }
            if (event.detail.contactFormId == 211 || event.detail.contactFormId == 215) {
                console.log('sign');
                dataLayer.push({'formOK':'ok', 'event':'singUpOK'});
            }
            if (event.detail.contactFormId == 5) {
                console.log('bottom');
                dataLayer.push({'formOK':'ok', 'event':'toCallOK'});
            }
        }
      }, false );
</script>
